import { useEffect, useState } from "react";
import Welcomepage from "./Welcomepage";
import { useNavigate } from "react-router-dom";

function Loginpage() {
  let [inputs, setInputs] = useState({});
  let [username, setUsername] = useState("");
  const Change = (event) => {
    const name = event.target.name;
    const value = event.target.value;
    setInputs((values) => ({ ...values, [name]: value }));
  };

  const handleClick = (event) => {
    Welcome();
    event.preventDefault();
    console.log(inputs);
  };

  function Welcome() {
    alert(`Welcome to Splitwise ${inputs.username}`);
    return (
      <>
        navigate( <Welcomepage name={username} />)
      </>
    );
  }
  useEffect(() => {
    setUsername(inputs.username);
    console.log(username);
  }, [username]);
  return (
    <>
      <div className="container">
        <div className="row">
          <div className="col mt-3">Log in to SplitWise</div>
          <div className="col-12 mt-2">
            <input
              className="ml-1"
              type="text"
              name="username"
              value={inputs.username || ""}
              placeholder=" Username"
              onChange={Change}
            />
          </div>

          <div className="col-12 mt-3">
            <input
              className="ml-1"
              type="password"
              name="password"
              value={inputs.password || ""}
              placeholder="Password"
              onChange={Change}
            />
          </div>

          <div className="col mt-5 mb-5 ">
            <button className="btn btn-primary px-5" onClick={handleClick}>
              Login
            </button>
          </div>
        </div>
      </div>
    </>
  );
}

export default Loginpage;
