import { useState } from "react";
import { Routes, Route, BrowserRouter } from "react-router-dom";
import ReactDOM from "react-dom/client";
import "./index.css";
import Welcomepage from "./pages/firstpage";
function App() {
  let [inputs, setInputs] = useState({});
  // const inputElement = useRef();
  const Change = (event) => {
    const name = event.target.name;
    const value = event.target.value;
    setInputs((values) => ({ ...values, [name]: value }));
  };
  // const focusInput = () => {
  //   inputElement.current.focus();
  // };
  const handleClick = (event) => {
    event.preventDefault();
    console.log(inputs);
    // focusInput();
    Welcome();
  };
  const Welcome = () => {
    alert(`Welcome to Splitwise ${inputs.username}`);
    return (
      <BrowserRouter>
        <Routes>
          <Route
            path="Welcome"
            element={<Welcomepage Name={inputs.username} />}
          ></Route>
        </Routes>
      </BrowserRouter>
    );
  };

  return (
    <>
      <form className="container" onSubmit={handleClick}>
        <div className="row">
          <div className="col mt-3">Log in to SplitWise</div>
          <div className="col-12 mt-2">
            <input
              className="ml-1"
              type="text"
              name="username"
              value={inputs.username || ""}
              placeholder=" Username"
              onChange={Change}
            />
          </div>

          <div className="col-12 mt-3">
            <input
              className="ml-1"
              type="password"
              name="password"
              value={inputs.password || ""}
              placeholder="Password"
              onChange={Change}
            />
          </div>

          <div className="col mt-5 mb-5 ">
            <button className="btn btn-primary px-5">Login</button>
          </div>
        </div>
      </form>
    </>
  );
}
const root = ReactDOM.createRoot(document.getElementById("root"));
root.render(<App />);
