import { useEffect, useState } from "react";
// import { useNavigate } from "react-router-dom";

function Loginpage() {
  const [inputs, setInputs] = useState({});
  const [username, setUsername] = useState();
  const[type,setType]=useState(password)

  // const navigate = useNavigate();
  const Change = (event) => {
    const name = event.target.name;
    const value = event.target.value;
    setInputs((values) => ({ ...values, [name]: value }));
  };

  const Eye=()=>{
    setType(text)
  }
  const handleClick = (event) => {
    Welcome();
    event.preventDefault();
    console.log(inputs);
  };

  function Welcome() {
    alert(`Welcome to Splitwise ${inputs.username}`);
    window.location("/Welcomepage");
  }
  useEffect(() => {
    setUsername(inputs.username);
  }, [inputs]);
  return (
    <>
      <form className="container" onSubmit={handleClick}>
        <div className="row">
          <div className="col-12 mt-3">Log in to SplitWise</div>
          <div className=" mt-2 ">
            <input
              className="ml-5 w-10"
              type="text"
              name="username"
              value={inputs.username || ""}
              placeholder=" Username"
              onChange={Change}
            />
          </div>

          <div className=" mt-3 ml-5 outline1 col-xs-1">
            <input
              className=" outline p-0"
              type={type}
              name="password"
              value={inputs.password || ""}
              placeholder="Password"
              onChange={Change}
            />
            <i class="fa-regular fa-eye" onClick={Eye}></i>
          </div>

          <div className="col mt-5 mb-5 ">
            <button className="btn btn-primary px-5">Login</button>
          </div>
        </div>
      </form>
    </>
  );
}
export default Loginpage;
