import { useEffect, useState, createContext } from "react";
import { useNavigate } from "react-router-dom";
import { Routes, Route, BrowserRouter } from "react-router-dom";
import Welcomepage from "./Welcomepage";
import NoPage from "./NoPageNoPage";
import { NameProvider } from "./Nameprovider";
// import { setUsername1 } from "../Nameprovider";

function Loginpage() {
  const [inputs, setInputs] = useState({});
  const [username, setUsername] = useState();
  const NameContext = createContext();
  const navigate = useNavigate();
  const Change = (event) => {
    const name = event.target.name;
    const value = event.target.value;
    setInputs((values) => ({ ...values, [name]: value }));
  };

  const handleClick = (event) => {
    Welcome();
    event.preventDefault();
    console.log(inputs);
  };

  function Welcome() {
    alert(`Welcome to Splitwise ${inputs.username}`);
    navigate("/Welcomepage");
    // return setUsername1(inputs.username);
  }
  useEffect(() => {
    setUsername(inputs.username);
  }, [inputs]);
  return (
    <>
      <form className="container" onSubmit={handleClick}>
        <div className="row">
          <div className="col mt-3">Log in to SplitWise</div>
          <div className="col-12 mt-2">
            <input
              className="ml-1"
              type="text"
              name="username"
              value={inputs.username || ""}
              placeholder=" Username"
              onChange={Change}
            />
          </div>

          <div className="col-12 mt-3">
            <input
              className="ml-1"
              type="password"
              name="password"
              value={inputs.password || ""}
              placeholder="Password"
              onChange={Change}
            />
          </div>

          <div className="col mt-5 mb-5 ">
            <button className="btn btn-primary px-5">Login</button>
          </div>
        </div>
      </form>
    </>
  );
}
function App() {
  return (
    <>
      <NameProvider>
        <BrowserRouter>
          <Routes>
            <Route path="/" element={<Loginpage />}></Route>
            <Route path="/Welcomepage" element={<Welcomepage />}></Route>
            <Route path="*" element={<NoPage />}></Route>
          </Routes>
        </BrowserRouter>
      </NameProvider>
    </>
  );
}
export default Loginpage;
